<?phpclass ControllerProductFntCustomerDesign extends Controller{    public function index()    {        $this->language->load('product/fnt_customer_design');        $this->document->setTitle($this->language->get('heading_title'));        $this->data['breadcrumbs']   = array();        $this->data['breadcrumbs'][] = array(            'text' => $this->language->get('text_home'),            'href' => $this->url->link('common/home'),            'separator' => false        );        $this->data['text_display']  = $this->language->get('text_display');        $this->data['text_list']     = $this->language->get('text_list');        $this->data['text_tax']      = $this->language->get('text_tax');        $this->data['text_grid']     = $this->language->get('text_grid');        $this->data['text_compare']  = sprintf($this->language->get('text_compare'), (isset($this->session->data['compare']) ? count($this->session->data['compare']) : 0));        $url                         = '';        $this->data['compare']       = $this->url->link('product/compare');        if (isset($this->request->get['limit'])) {            $url .= '&limit=' . $this->request->get['limit'];        }        if (isset($this->request->get['sort'])) {            $url .= '&sort=' . $this->request->get['sort'];        }        if (isset($this->request->get['order'])) {            $url .= '&order=' . $this->request->get['order'];        }        if (isset($this->request->get['page'])) {            $url .= '&page=' . $this->request->get['page'];        }        $this->data['breadcrumbs'][] = array(            'text' => 'Customer Designs',            'href' => $this->url->link('product/fnt_customer_design'),            'separator' => ''        );        $this->data['heading_title'] = $this->language->get('heading_title');        $this->data['text_sort']     = $this->language->get('text_sort');        $this->data['text_limit']    = $this->language->get('text_limit');        if (isset($this->request->get['page'])) {            $page = $this->request->get['page'];        } else {            $page = 1;        }        if (isset($this->request->get['sort'])) {            $sort = $this->request->get['sort'];        } else {            $sort = 'p.date_added';        }        if (isset($this->request->get['order'])) {            $order = $this->request->get['order'];        } else {            $order = 'DESC';        }        if (isset($this->request->get['limit'])) {            $limit = $this->request->get['limit'];        } else {            $limit = '32';        }        $this->load->model('catalog/fnt_customer_design');        $product_total = $this->model_catalog_fnt_customer_design->getTotalProduct();        if ($product_total) {            $url = '';            $this->load->model('catalog/review');            $this->load->model('tool/image');            $this->data['button_cart']     = $this->language->get('button_cart');            $this->data['button_wishlist'] = $this->language->get('button_wishlist');            $this->data['button_compare']  = $this->language->get('button_compare');            $this->data['button_continue'] = $this->language->get('button_continue');            $this->data['products']        = array();            $data                          = array(                'sort' => $sort,                'order' => $order,                'start' => ($page - 1) * $limit,                'limit' => $limit            );            $results                       = $this->model_catalog_fnt_customer_design->getAllProductCustomerDesign();            foreach ($results as $result) {                if ($result['image']) {                    $image = $result['image'];                } else {                    $image = 'no_image.jpg';                }                if (($this->config->get('config_customer_price') && $this->customer->isLogged()) || !$this->config->get('config_customer_price')) {                    $price = $this->currency->format($this->tax->calculate($result['price'], $result['tax_class_id'], $this->config->get('config_tax')));                } else {                    $price = false;                }                if ($this->config->get('config_review')) {                    $rating = $this->model_catalog_review->getAverageRating($result['product_id']);                } else {                    $rating = false;                }                /*check price special*/                $special_price = $this->model_catalog_fnt_customer_design->getProductSpecial($result['product_id']);                if ((float) $special_price) {                    $special = $this->currency->format($this->tax->calculate((float) $special_price, $result['tax_class_id'], $this->config->get('config_tax')));                } else {                    $special = false;                }                //end check                if ($this->config->get('config_tax')) {                    $tax = $this->currency->format((float) $special_price ? $special_price : $result['price']);                } else {                    $tax = false;                }                if ($result['product_customer_idea_accept_id']) {                    $design = $this->url->link('product/fnt_category_product_design', 'product_customer_idea_accept_id=' . $result['product_customer_idea_accept_id']);                } else {                    $design = false;                }                $this->data['products'][] = array(                    'name' => $result['name'],                    'design' => $design,                    'product_id' => $result['product_id'],                    'thumb' => (class_exists('HelperImage')) ? HelperImage::resize($image, $this->config->get('config_image_product_width'), $this->config->get('config_image_product_height')) : (class_exists('ModelToolImage')) ? $this->model_tool_image->resize($image, $this->config->get('config_image_product_width'), $this->config->get('config_image_product_height')) : image_resize($image, $this->config->get('config_image_product_width'), $this->config->get('config_image_product_height')),                    'price' => $this->currency->format($this->tax->calculate($result['price'], $result['tax_class_id'], $this->config->get('config_tax'))),                    'special' => $special,                    'tax' => $tax,                    'href' => $design                );            }            if (!$this->config->get('config_customer_price')) {                $this->data['display_price'] = TRUE;            } elseif ($this->customer->isLogged()) {                $this->data['display_price'] = TRUE;            } else {                $this->data['display_price'] = FALSE;            }            $url = '';            if (isset($this->request->get['limit'])) {                $url = '&limit=' . $this->request->get['limit'];            }            $this->data['sorts']    = array();            $this->data['sorts'][]  = array(                'text' => $this->language->get('text_newest'),                'value' => 'p.date_available-DESC',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=p.date_available&order=DESC')            );            $this->data['sorts'][]  = array(                'text' => $this->language->get('text_name_asc'),                'value' => 'pd.name',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=pd.name')            );            $this->data['sorts'][]  = array(                'text' => $this->language->get('text_name_desc'),                'value' => 'pd.name-DESC',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=pd.name&order=DESC')            );            $this->data['sorts'][]  = array(                'text' => $this->language->get('text_price_asc'),                'value' => 'p.price-ASC',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=p.price&order=ASC')            );            $this->data['sorts'][]  = array(                'text' => $this->language->get('text_price_desc'),                'value' => 'p.price-DESC',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=p.price&order=DESC')            );            $this->data['sorts'][]  = array(                'text' => $this->language->get('text_rating_desc'),                'value' => 'rating-DESC',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=rating&order=DESC')            );            $this->data['sorts'][]  = array(                'text' => $this->language->get('text_rating_asc'),                'value' => 'rating-ASC',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=rating&order=ASC')            );            $this->data['sorts'][]  = array(                'text' => $this->language->get('text_oldest'),                'value' => 'p.date_available-ASC',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=p.date_available&order=ASC')            );            $url                    = '';            $this->data['limits']   = array();            $this->data['limits'][] = array(                'text' => '32',                'value' => '32',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&limit=32')            );            $this->data['limits'][] = array(                'text' => '50',                'value' => '50',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&limit=50')            );            $this->data['limits'][] = array(                'text' => '75',                'value' => '75',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&limit=75')            );            $this->data['limits'][] = array(                'text' => '100',                'value' => '100',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&limit=100')            );            $url                    = '';            if (isset($this->request->get['sort'])) {                $url .= '&sort=' . $this->request->get['sort'];            }            if (isset($this->request->get['order'])) {                $url .= '&order=' . $this->request->get['order'];            }            if (isset($this->request->get['limit'])) {                $url .= '&limit=' . $this->request->get['limit'];            }            if (isset($this->request->get['page']) && $product_total < $limit) {                $url .= '&page=' . $this->request->get['page'];            }            $pagination               = new Pagination();            $pagination->total        = $product_total;            $pagination->page         = $page;            $pagination->limit        = $limit;            $pagination->text         = $this->language->get('text_pagination');            $pagination->url          = HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&page={page}';            $this->data['pagination'] = $pagination->render();            $this->data['sort']       = $sort;            $this->data['order']      = $order;            $this->data['limit']      = $limit;            $this->id                 = 'content';            if (file_exists(DIR_TEMPLATE . $this->config->get('config_template') . '/template/product/fnt_customer_design.tpl')) {                $this->template = $this->config->get('config_template') . '/template/product/fnt_customer_design.tpl';            } else {                $this->template = 'default/template/product/fnt_customer_design.tpl';            }            $this->children = array(                'common/column_left',                'common/column_right',                'common/content_top',                'common/content_bottom',                'common/footer',                'common/header'            );            $this->response->setOutput($this->render(TRUE), $this->config->get('config_compression'));        } else {            $this->data['text_error']      = $this->language->get('text_empty');            $this->data['button_continue'] = $this->language->get('button_continue');            $this->data['continue']        = (HTTP_SERVER . 'index.php?route=common/home');            $this->id                      = 'content';            if (file_exists(DIR_TEMPLATE . $this->config->get('config_template') . '/template/error/not_found.tpl')) {                $this->template = $this->config->get('config_template') . '/template/error/not_found.tpl';            } else {                $this->template = 'default/template/error/not_found.tpl';            }            $this->children = array(                'common/column_left',                'common/column_right',                'common/content_top',                'common/content_bottom',                'common/footer',                'common/header'            );            $this->response->setOutput($this->render(TRUE), $this->config->get('config_compression'));        }    }}?>